module.exports = {
    "aliases": ["binary", "bin", "b"],
    "scripts": {
      "translateTo": function (a) {
        return a.split("").map(b => b.charCodeAt().toString(2)).map(b => {
          while (b.length < 8) {
            b = "0" + b
          }
          return b;
        }).join(" ");
      },
      "translateFrom": function (a) {
        return a.split(" ").map(b => String.fromCharCode(parseInt(b, 2))).join("");
      }
    }
  }