module.exports = {
    "aliases": ["hex", "x"],
    "scripts": {
      "translateTo": function (a) {
        return a.split("").map(b => b.charCodeAt().toString(16).toUpperCase()).join(" ");
      },
      "translateFrom": function (a) {
        return a.split(" ").map(b => String.fromCharCode(parseInt(b, 16))).join("");
      }
    }
  }