module.exports = {
    "aliases": ["ternary", "3", "t"],
    "scripts": {
      "translateTo": function(a) {
        return a.split("").map(b => b.charCodeAt().toString(3)).map(b => {
          while (b.length < 6) {
            b = "0" + b
          }
          return b;
        }).join(" ");
      },
      "translateFrom": function (a) {
        return a.split(" ").map(b => String.fromCharCode(parseInt(b, 3))).join("");
      }
    }
  }